import{_ as t,o as e,c as i,ag as n}from"./chunks/framework.DPLcbSfg.js";const g=JSON.parse('{"title":"管理员 API","description":"","frontmatter":{},"headers":[],"relativePath":"api/admin.md","filePath":"api/admin.md","lastUpdated":null}'),o={name:"api/admin.md"};function s(l,a,d,r,m,c){return e(),i("div",null,[...a[0]||(a[0]=[n('<h1 id="管理员-api" tabindex="-1">管理员 API <a class="header-anchor" href="#管理员-api" aria-label="Permalink to &quot;管理员 API&quot;">​</a></h1><p>管理员接口用于登录后台、查看和管理评论、配置邮件通知和评论显示设置。</p><p>除登录接口外，所有管理员接口都需要在请求头中携带 Bearer Token。</p><div class="language-http vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">http</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#22863A;--shiki-dark:#85E89D;">Authorization</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">:</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> Bearer &lt;token&gt;</span></span></code></pre></div><p>Token 通过登录接口获取，有效期为 24 小时。</p><h2 id="接口分类" tabindex="-1">接口分类 <a class="header-anchor" href="#接口分类" aria-label="Permalink to &quot;接口分类&quot;">​</a></h2><h3 id="身份认证相关" tabindex="-1"><a href="./admin/auth.html">身份认证相关</a> <a class="header-anchor" href="#身份认证相关" aria-label="Permalink to &quot;[身份认证相关](./admin/auth.md)&quot;">​</a></h3><ul><li><strong>管理员登录</strong> <code>POST /admin/login</code> - 获取后续调用其他管理员接口所需的临时 Token</li></ul><h3 id="评论管理相关" tabindex="-1"><a href="./admin/comments.html">评论管理相关</a> <a class="header-anchor" href="#评论管理相关" aria-label="Permalink to &quot;[评论管理相关](./admin/comments.md)&quot;">​</a></h3><ul><li><strong>获取评论列表</strong> <code>GET /admin/comments/list</code> - 获取评论列表，用于后台管理页面展示</li><li><strong>更新评论状态</strong> <code>PUT /admin/comments/status</code> - 更新评论状态（例如通过 / 拒绝）</li><li><strong>更新评论内容</strong> <code>PUT /admin/comments/update</code> - 更新评论的详细信息</li><li><strong>删除指定评论</strong> <code>DELETE /admin/comments/delete</code> - 删除指定评论</li><li><strong>加入 IP 黑名单</strong> <code>POST /admin/comments/block-ip</code> - 将指定 IP 加入评论黑名单</li><li><strong>加入邮箱黑名单</strong> <code>POST /admin/comments/block-email</code> - 将指定邮箱加入评论黑名单</li></ul><h3 id="数据迁移相关" tabindex="-1"><a href="./admin/data-migration.html">数据迁移相关</a> <a class="header-anchor" href="#数据迁移相关" aria-label="Permalink to &quot;[数据迁移相关](./admin/data-migration.md)&quot;">​</a></h3><ul><li><strong>导出所有评论数据</strong> <code>GET /admin/comments/export</code> - 导出所有评论数据</li><li><strong>导入评论数据</strong> <code>POST /admin/comments/import</code> - 导入评论数据</li></ul><h3 id="评论设置相关" tabindex="-1"><a href="./admin/settings.html">评论设置相关</a> <a class="header-anchor" href="#评论设置相关" aria-label="Permalink to &quot;[评论设置相关](./admin/settings.md)&quot;">​</a></h3><ul><li><strong>获取评论配置</strong> <code>GET /admin/settings/comments</code> - 获取评论配置</li><li><strong>更新评论配置</strong> <code>PUT /admin/settings/comments</code> - 更新评论配置</li></ul><h3 id="邮件通知配置相关" tabindex="-1"><a href="./admin/email-notify.html">邮件通知配置相关</a> <a class="header-anchor" href="#邮件通知配置相关" aria-label="Permalink to &quot;[邮件通知配置相关](./admin/email-notify.md)&quot;">​</a></h3><ul><li><strong>获取邮件通知配置</strong> <code>GET /admin/settings/email-notify</code> - 获取邮件通知配置</li><li><strong>更新邮件通知配置</strong> <code>PUT /admin/settings/email-notify</code> - 更新邮件通知配置</li><li><strong>测试邮件发送</strong> <code>POST /admin/settings/email-test</code> - 测试邮件通知配置是否正确</li></ul><h3 id="统计数据相关" tabindex="-1"><a href="./admin/stats.html">统计数据相关</a> <a class="header-anchor" href="#统计数据相关" aria-label="Permalink to &quot;[统计数据相关](./admin/stats.md)&quot;">​</a></h3><ul><li><strong>获取评论统计数据</strong> <code>GET /admin/stats/comments</code> - 用于管理后台「数据看板」展示评论整体统计</li><li><strong>获取域名列表</strong> <code>GET /admin/stats/domains</code> - 获取所有已产生评论或访问记录的域名列表</li></ul><h3 id="访问统计相关" tabindex="-1"><a href="./admin/analytics.html">访问统计相关</a> <a class="header-anchor" href="#访问统计相关" aria-label="Permalink to &quot;[访问统计相关](./admin/analytics.md)&quot;">​</a></h3><ul><li><strong>获取访问统计概览</strong> <code>GET /admin/analytics/overview</code> - 用于管理后台「访问统计」页面展示整体访问数据</li><li><strong>获取页面访问统计</strong> <code>GET /admin/analytics/pages</code> - 用于管理后台「访问统计」页面展示各个页面的访问明细</li></ul><h3 id="点赞管理相关" tabindex="-1">点赞管理相关 <a class="header-anchor" href="#点赞管理相关" aria-label="Permalink to &quot;点赞管理相关&quot;">​</a></h3><ul><li><strong>获取点赞记录列表</strong> <code>GET /admin/likes/list</code> - 获取各页面的点赞记录列表，支持按页面或用户筛选</li><li><strong>获取点赞统计</strong> <code>GET /admin/likes/stats</code> - 获取点赞 Top 页面列表，用于后台展示点赞排行榜</li></ul><h3 id="功能设置相关" tabindex="-1"><a href="./admin/feature-settings.html">功能设置相关</a> <a class="header-anchor" href="#功能设置相关" aria-label="Permalink to &quot;[功能设置相关](./admin/feature-settings.md)&quot;">​</a></h3><ul><li><strong>获取功能设置</strong> <code>GET /admin/settings/feature</code> - 获取功能开关设置（评论点赞、文章点赞）</li><li><strong>更新功能设置</strong> <code>PUT /admin/settings/feature</code> - 更新功能开关设置</li></ul>',24)])])}const u=t(o,[["render",s]]);export{g as __pageData,u as default};
