import{_ as d,o as e,c as a,ag as o}from"./chunks/framework.rAhUSuuf.js";const m=JSON.parse('{"title":"邮箱提醒","description":"","frontmatter":{},"headers":[],"relativePath":"function/email-reminder.md","filePath":"function/email-reminder.md","lastUpdated":null}'),r={name:"function/email-reminder.md"};function l(i,t,c,h,n,s){return e(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="邮箱提醒" tabindex="-1">邮箱提醒 <a class="header-anchor" href="#邮箱提醒" aria-label="Permalink to &quot;邮箱提醒&quot;">​</a></h1><p>目前支持进行通知的邮箱有：</p><ul><li>QQ 邮箱</li><li>163 邮箱</li><li>自定义 SMTP</li></ul><p>后续会添加其他邮箱服务，可以提交反馈。</p><h2 id="邮箱服务配置" tabindex="-1">邮箱服务配置 <a class="header-anchor" href="#邮箱服务配置" aria-label="Permalink to &quot;邮箱服务配置&quot;">​</a></h2><h3 id="qq-邮箱" tabindex="-1">QQ 邮箱 <a class="header-anchor" href="#qq-邮箱" aria-label="Permalink to &quot;QQ 邮箱&quot;">​</a></h3><ol><li>登录 QQ 邮箱，进入 <code>设置 &gt; 账户</code></li><li>开启 <code>POP3/IMAP/SMTP/Exchange/CardDAV/CalDAV 服务</code>，并获取授权码</li><li>在管理后台设置中配置 QQ 邮箱账号和授权码</li></ol><h3 id="_163-邮箱" tabindex="-1">163 邮箱 <a class="header-anchor" href="#_163-邮箱" aria-label="Permalink to &quot;163 邮箱&quot;">​</a></h3><ol><li>登录 163 邮箱，进入 <code>设置</code></li><li>开启 <code>POP3/SMTP/IMAP</code>，并获取授权码</li><li>在管理后台设置中配置 163 邮箱账号和授权码</li></ol><h3 id="自定义-smtp-邮箱" tabindex="-1">自定义 SMTP 邮箱 <a class="header-anchor" href="#自定义-smtp-邮箱" aria-label="Permalink to &quot;自定义 SMTP 邮箱&quot;">​</a></h3><p>配置自己的 SMTP 邮箱需要以下信息：</p><ul><li>SMTP 服务器地址</li><li>SMTP 服务器端口</li><li>发件邮箱账号</li><li>邮箱密码或授权码</li></ul><h2 id="邮件通知配置" tabindex="-1">邮件通知配置 <a class="header-anchor" href="#邮件通知配置" aria-label="Permalink to &quot;邮件通知配置&quot;">​</a></h2><p>在后台「设置」页面可以配置邮件通知功能，包括：</p><h3 id="全局开关" tabindex="-1">全局开关 <a class="header-anchor" href="#全局开关" aria-label="Permalink to &quot;全局开关&quot;">​</a></h3><p>控制是否启用邮件通知功能。</p><h3 id="smtp-配置" tabindex="-1">SMTP 配置 <a class="header-anchor" href="#smtp-配置" aria-label="Permalink to &quot;SMTP 配置&quot;">​</a></h3><table tabindex="0"><thead><tr><th>字段</th><th>说明</th><th>示例</th></tr></thead><tbody><tr><td>主机</td><td>SMTP 服务器地址</td><td><code>smtp.qq.com</code></td></tr><tr><td>端口</td><td>SMTP 服务器端口</td><td><code>465</code></td></tr><tr><td>用户名</td><td>发件邮箱</td><td><code>noreply@qq.com</code></td></tr><tr><td>密码</td><td>邮箱密码或授权码</td><td><code>your_password</code></td></tr><tr><td>安全连接</td><td>是否使用 SSL/TLS</td><td><code>true</code></td></tr></tbody></table><h3 id="邮件模板" tabindex="-1">邮件模板 <a class="header-anchor" href="#邮件模板" aria-label="Permalink to &quot;邮件模板&quot;">​</a></h3><p>支持自定义 HTML 邮件模板，包含以下变量：</p><p><strong>回复评论模板变量</strong>：</p><table tabindex="0"><thead><tr><th>变量名</th><th>说明</th></tr></thead><tbody><tr><td><code>${toEmail}</code></td><td>收件人邮箱</td></tr><tr><td><code>${toName}</code></td><td>收件人昵称</td></tr><tr><td><code>${postTitle}</code></td><td>文章标题</td></tr><tr><td><code>${parentComment}</code></td><td>被回复的评论内容</td></tr><tr><td><code>${replyAuthor}</code></td><td>回复者昵称</td></tr><tr><td><code>${replyContent}</code></td><td>回复内容</td></tr><tr><td><code>${postUrl}</code></td><td>文章链接</td></tr></tbody></table><p><strong>新评论通知模板变量</strong>：</p><table tabindex="0"><thead><tr><th>变量名</th><th>说明</th></tr></thead><tbody><tr><td><code>${postTitle}</code></td><td>文章标题</td></tr><tr><td><code>${postUrl}</code></td><td>文章链接</td></tr><tr><td><code>${commentAuthor}</code></td><td>评论者昵称</td></tr><tr><td><code>${commentContent}</code></td><td>评论内容</td></tr></tbody></table><h3 id="测试邮件" tabindex="-1">测试邮件 <a class="header-anchor" href="#测试邮件" aria-label="Permalink to &quot;测试邮件&quot;">​</a></h3><p>配置完成后，可以使用「发送测试邮件」功能验证配置是否正确。</p><p>测试邮件和实际的新评论通知收件人，都会使用「评论显示配置」中的管理员邮箱（<code>adminEmail</code>）。无需在「通知邮箱设置」中单独配置收件邮箱。</p>',27)])])}const u=d(r,[["render",l]]);export{m as __pageData,u as default};
