import{_ as t,o as l,c as o,ag as a}from"./chunks/framework.rAhUSuuf.js";const h=JSON.parse('{"title":"Telegram 通知与交互","description":"","frontmatter":{},"headers":[],"relativePath":"function/telegram-notify.md","filePath":"function/telegram-notify.md","lastUpdated":null}'),r={name:"function/telegram-notify.md"};function i(n,e,s,g,d,c){return l(),o("div",null,[...e[0]||(e[0]=[a('<h1 id="telegram-通知与交互" tabindex="-1">Telegram 通知与交互 <a class="header-anchor" href="#telegram-通知与交互" aria-label="Permalink to &quot;Telegram 通知与交互&quot;">​</a></h1><p>系统支持通过 Telegram 机器人接收新评论通知，并在 Telegram 中对评论进行审核（批准）操作。</p><h2 id="功能特性" tabindex="-1">功能特性 <a class="header-anchor" href="#功能特性" aria-label="Permalink to &quot;功能特性&quot;">​</a></h2><ol><li><strong>实时通知</strong>：新评论会立即推送到指定的 Telegram 账号或群组。</li><li><strong>快捷审核</strong>：通知消息包含“批准”按钮，无需登录后台即可审核。</li></ol><h2 id="配置步骤" tabindex="-1">配置步骤 <a class="header-anchor" href="#配置步骤" aria-label="Permalink to &quot;配置步骤&quot;">​</a></h2><ol><li><p><strong>创建机器人</strong>：</p><ul><li>在 Telegram 中搜索 <code>@BotFather</code>。</li><li>发送 <code>/newbot</code> 命令，按照提示创建新机器人。</li><li>获取 <strong>API Token</strong>。</li></ul></li><li><p><strong>获取 Chat ID</strong>：</p><ul><li>如果是个人接收，给你的机器人发送一条消息。</li><li>访问 <code>https://api.telegram.org/bot&lt;YourBOTToken&gt;/getUpdates</code> 查看 <code>chat.id</code>。</li><li>或者使用 <code>@userinfobot</code> 获取你的 ID。</li><li>如果是群组，将机器人拉入群组，并获取群组 ID（通常以 <code>-</code> 开头）。</li></ul></li><li><p><strong>后台设置</strong>：</p><ul><li>登录评论系统后台。</li><li>进入“设置” -&gt; “Telegram 通知设置”。</li><li>填入 <strong>Bot Token</strong> 和 <strong>Chat ID</strong>。</li><li>开启 <strong>Telegram 通知</strong> 开关。</li><li>点击 <strong>保存</strong>。</li><li>点击 <strong>一键设置 Webhook</strong>（重要：必须执行此步骤，机器人才能接收按钮点击事件）。</li></ul></li></ol><h2 id="使用说明" tabindex="-1">使用说明 <a class="header-anchor" href="#使用说明" aria-label="Permalink to &quot;使用说明&quot;">​</a></h2><ul><li><strong>审核</strong>：在 Telegram 中点击消息下方的“批准”按钮即可完成审核操作。</li></ul>',8)])])}const u=t(r,[["render",i]]);export{h as __pageData,u as default};
